import { Component, OnInit } from '@angular/core';
import { KeycloakService } from 'keycloak-angular';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'projectnew';
  isKeycloakInitialized = false;  // Flag to track Keycloak initialization status

  constructor(private keycloakService: KeycloakService) {}

  ngOnInit() {
    // Initialize Keycloak and track its status
    this.keycloakService.init({
      config: {
        url: 'https://oridess.mmthinkbiz.com:8443',
        realm: 'amazon-oidc-app',
        clientId: 'localhost-app',
      },
      initOptions: {
        onLoad: 'login-required',
        checkLoginIframe: false,
      }
    }).then(() => {
      this.isKeycloakInitialized = true;
      console.log('Keycloak initialized successfully');
    }).catch((error) => {
      console.error('Keycloak initialization failed', error);
    });
  }

  login() {
    if (this.isKeycloakInitialized) {
      this.keycloakService.login();
    } else {
      console.log('Keycloak is not initialized yet.');
    }
  }

  logout() {
    if (this.isKeycloakInitialized) {
      this.keycloakService.logout();
    } else {
      console.log('Keycloak is not initialized yet.');
    }
  }

  getUsername(): string {
    if (this.isKeycloakInitialized) {
      return this.keycloakService.getUsername();
    }
    return '';
  }
}
<button *ngIf="isKeycloakInitialized" (click)="login()">Login</button>
<button *ngIf="isKeycloakInitialized" (click)="logout()">Logout</button>

<p *ngIf="isKeycloakInitialized">Logged in user: {{ getUsername() }}</p>

<!-- Show a loading message when Keycloak is initializing -->
<p *ngIf="!isKeycloakInitialized">Loading Keycloak...</p>
