import { Component, OnInit } from '@angular/core';
import { KeycloakService } from 'keycloak-angular';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'projectnew';
  isKeycloakInitialized = false;

  constructor(private keycloakService: KeycloakService) {}

  ngOnInit() {
    this.keycloakService.isInitialized().then(() => {
      this.isKeycloakInitialized = true;
      console.log('Keycloak initialization complete.');
    }).catch(error => {
      console.error('Keycloak initialization failed', error);
    });
  }

  login() {
    if (this.isKeycloakInitialized) {
      this.keycloakService.login();
    } else {
      console.log('Keycloak is not initialized yet.');
    }
  }

  logout() {
    if (this.isKeycloakInitialized) {
      this.keycloakService.logout();
    } else {
      console.log('Keycloak is not initialized yet.');
    }
  }

  getUsername(): string {
    if (this.isKeycloakInitialized) {
      return this.keycloakService.getUsername();
    }
    return '';
  }
}
