import { Component } from '@angular/core';
import { KeycloakService } from 'keycloak-angular';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  isKeycloakInitialized = false;

  constructor(private keycloakService: KeycloakService) {}

  async login(idpHint: string) {
    const clientId = 'localhost-app';
    const redirectUri = encodeURIComponent(window.location.origin); // Dynamic Redirect URI
    const authUrl = `https://oridess.mmthinkbiz.com:8443/realms/amazon-oidc-app/protocol/openid-connect/auth` +
      `?client_id=${clientId}` +
      `&redirect_uri=${redirectUri}` +
      `&response_type=code` +
      `&kc_idp_hint=${idpHint}`;

    // Redirect to Keycloak login page
    window.location.href = authUrl;
  }


  async logout() {
    await this.keycloakService.logout();
    this.isKeycloakInitialized = false;
  }

  getUsername(): string {
    return this.keycloakService.getUsername() || 'Unknown User';
  }
}
<!-- Login Buttons with Different Identity Providers -->
<button (click)="login('google')">Login with Google</button>
<button (click)="login('amazon-oidc-app')">Login with Amazon OIDC</button>

<!-- Logout Button -->
<button *ngIf="isKeycloakInitialized" (click)="logout()">Logout</button>

<!-- Display Logged-in User -->
<p *ngIf="isKeycloakInitialized">Logged in user: {{ getUsername() }}</p>

<!-- Show message before login -->
<p *ngIf="!isKeycloakInitialized">Click Login to authenticate</p>
