import { NgModule,APP_INITIALIZER } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { KeycloakAngularModule, KeycloakService } from 'keycloak-angular';

function initializeKeycloak(keycloak: KeycloakService) {
  return () =>
    keycloak.init({
      config: {
        url: 'https://oridess.mmthinkbiz.com:8443',  // Keycloak Server URL
        realm: 'amazon-oidc-app',  // Keycloak Realm Name
        clientId: 'orides-app',    // Keycloak Client ID
      },
      initOptions: {
        onLoad: 'login-required', // Forces login
        checkLoginIframe: false,  // Disable login iframe for compatibility
      },
    });
}
@NgModule({
  declarations: [
    AppComponent
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    KeycloakAngularModule
  ],
  providers: [
    {
      provide: APP_INITIALIZER,
      useFactory: initializeKeycloak,
      deps: [KeycloakService],
      multi: true
    }
  ],
  bootstrap: [AppComponent]
})
export class AppModule { }
