import { Component } from '@angular/core';
interface TripSegment {
  start: string;
  end: string;
  level: number;
  continued: boolean;
  sameRoute: boolean;
  arrow: boolean;
  shortStart: string;
  shortEnd: string;
  color: string;
}
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  startPoint = '';
  endPoint = '';
  trips: TripSegment[] = [];

  addTrip() {
    const shortStart = this.startPoint.trim().substring(0, 3).toUpperCase();
    const shortEnd = this.endPoint.trim().substring(0, 3).toUpperCase();

    const newTrip: TripSegment = {
      start: this.startPoint.trim(),
      end: this.endPoint.trim(),
      level: 1,
      continued: false,
      sameRoute: false,
      arrow: false,
      shortStart,
      shortEnd,
      color: '#888',
    };

    const lastTrip = this.trips[this.trips.length - 1];

    if (lastTrip) {
      newTrip.continued = lastTrip.end === newTrip.start;
      newTrip.sameRoute = lastTrip.start === newTrip.start && lastTrip.end === newTrip.end;
      newTrip.arrow = !newTrip.continued;
      newTrip.level = newTrip.sameRoute ? 2 : 1;

      if (newTrip.sameRoute) {
        newTrip.color = '#a0a0a0';
      } else if (newTrip.arrow) {
        newTrip.color = '#2196f3';
      } else {
        newTrip.color = '#ffb74d';
      }
    } else {
      newTrip.color = '#5c6bc0';
    }

    this.trips.push(newTrip);
    this.startPoint = '';
    this.endPoint = '';
  }
}
