import { Component } from '@angular/core';
import { KeycloakService } from 'keycloak-angular';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  isKeycloakInitialized = false;

  constructor(private keycloakService: KeycloakService) {}

  async login() {
    try {
      await this.keycloakService.init({
        config: {
          url: 'https://oridess.:8080/realms/amazon-oidc-app/protocol/openid-connect/auth?response_type=code&client_id=orides-app',
          realm: 'amazon-oidc-app',
          clientId: 'localhost-app',
        },
        initOptions: {
          onLoad: 'login-required',
          checkLoginIframe: false,
        },
      });
      this.isKeycloakInitialized = true;
    } catch (error) {
      console.error('Keycloak initialization failed', error);
    }
  }

  async logout() {
    await this.keycloakService.logout();
    this.isKeycloakInitialized = false;
  }

  getUsername(): string {
    return this.keycloakService.getUsername();
  }
}
<button (click)="login()">Login</button>
<button *ngIf="isKeycloakInitialized" (click)="logout()">Logout</button>

<p *ngIf="isKeycloakInitialized">Logged in user: {{ getUsername() }}</p>

<!-- Show a loading message while Keycloak is initializing -->
<p *ngIf="!isKeycloakInitialized">Click Login to authenticate</p>
